void		Brain::deepCopy( const Brain& src )
{
	for (int i=0; i<100; ++i)
		_ideas[i] = src._ideas[i];
}

void		Brain::firstFiveIdeas( void ) const
{
	for (int i=0; i<5; ++i)
		std::cout << "[" << i + 1 << "] " << _ideas[i] <<std::endl;

}
